trigger.body:
    vCount NUMBER;
BEGIN

    IF AD_isTriggerEnabled()='N' THEN RETURN;
    END IF;

    IF TG_OP IN ('INSERT','UPDATE') AND :NEW.value = :NEW.taxid THEN
        SELECT COUNT(c_bpartner_id) INTO vCount FROM c_bpartner WHERE taxid = :NEW.taxid AND value = :NEW.taxid;
        IF vCount > 1 THEN
            RAISE_APPLICATION_ERROR(-20000, '@SCACTU_DuplicateTaxID@');
        END IF;
    END IF;

    END SCACTU_UNIQUE_TAXID_TRG
